
public class Star {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
			
		
		
		int row=5;
		for (int i= 0;i<= row;    i++) {
			 
			for(int j=  row-i;j >=1;  j--) {
				
				System.out.println("  ");
			}
			for(int k= 0;k  <=i;     k++) {
				System.out.println(k+"  ");
			}
			System.out.println();
			}


			}
	}
