
public class Starprint1 {
	public static void rightTriangle(int n) {
		
	
	int i,j;
	for(i=0;i<n;i++)
	{
		 System.out.print(" ");
	}{
		
		for(j=0;j<=i;j++);
		
		
			System.out.println("*");
			
	}

    System.out.println(); //ending line after each row
	
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n=5;
		rightTriangle(n);
	
		


	}

}
